import React, { useState } from 'react';
import './style.css';

const EggForm = () => {
  const [mode, setMode] = useState('collected');

  const [formData, setFormData] = useState({
    quantity: '',
    date: new Date().toISOString().split('T')[0],
    price: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.quantity || !formData.date || (mode === 'sold' && !formData.price)) {
      alert('Uzupełnij wymagane pola');
      return;
    }

    const data = {
      type: mode,
      quantity: parseInt(formData.quantity),
      date: formData.date,
      ...(mode === 'sold' && { price: parseFloat(formData.price) }),
    };

    console.log('Zapisz dane:', data);

    // Reset formularza
    setFormData({
      quantity: '',
      date: new Date().toISOString().split('T')[0],
      price: '',
    });
  };

  return (
    <form className="transaction-form glass-box" onSubmit={handleSubmit}>
      <h2>{mode === 'collected' ? 'Zebrane jajka' : 'Sprzedane jajka'}</h2>

      <div className="form-group">
        <label>Typ</label>
        <div>
          <label>
            <input
              type="radio"
              name="mode"
              value="collected"
              checked={mode === 'collected'}
              onChange={() => setMode('collected')}
            />
            Zebrane
          </label>
          <label style={{ marginLeft: '1rem' }}>
            <input
              type="radio"
              name="mode"
              value="sold"
              checked={mode === 'sold'}
              onChange={() => setMode('sold')}
            />
            Sprzedane
          </label>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="quantity">Ilość *</label>
        <input
          id="quantity"
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleChange}
          required
        />
      </div>

      {mode === 'sold' && (
        <div className="form-group">
          <label htmlFor="price">Kwota sprzedaży *</label>
          <input
            id="price"
            type="number"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>
      )}

      <div className="form-group">
        <label htmlFor="date">Data *</label>
        <input
          id="date"
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>

      <div className="form-actions">
        <button type="submit">Zapisz</button>
      </div>
    </form>
  );
};

export default EggForm;
